package assesment1;

public class oddOrEven {
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x = 13;
		
		int reminder = x % 2;
		
		if(reminder == 0) {
			System.out.println("given input is even");
		}
		else
			System.out.println("given input is odd");

	}

}
